// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";

interface IStrategy {
    event Deployed(bytes32 id, string name, string schema);
    event Initialize(bytes32 id, string name, address owner, bytes data, string schema);

    function initialize(address owner, bytes calldata data) external;

    function strategyName() external returns (string memory);

    function schema() external returns (string memory);
}

 contract Strategy is IStrategy, Initializable {
    bytes32 public id;
    string public strategyName;
    string public schema;

    constructor(string memory _name, string memory _schema) {
        strategyName = _name;
        schema = _schema;
        id = keccak256(abi.encode(strategyName));
        emit Deployed(id, strategyName, schema);
    }

    function initialize(address owner, bytes calldata data) public virtual initializer {
        emit Initialize(id, strategyName, owner, data, schema);
    }

    /**
     * Function that allows the contract to receive ETH
     */
    receive() external payable {}
}

contracts/extensions/Allocator.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {Context} from "@openzeppelin/contracts/utils/Context.sol";

interface IAllocator {
    event Allocate(address indexed from, address indexed to, uint256 amount, address token, bytes data);

    function allocate(address[] calldata recipients, uint256[] calldata amounts, address token, bytes[] calldata data)
        external;

    function distribute(address[] calldata recipients, uint256[] calldata amounts, address token, bytes[] calldata data)
        external;
}

contract Allocator is IAllocator, ReentrancyGuard, Context {
    using SafeERC20 for IERC20;

    bytes32 private constant OP_ALLOCATE = keccak256("allocate");
    bytes32 private constant OP_DISTRIBUTE = keccak256("distribute");

    function allocate(address[] memory recipients, uint256[] memory amounts, address token, bytes[] memory data)
        public
        virtual
    {
        _handleBatch(recipients, amounts, token, data, OP_ALLOCATE);
    }

    function distribute(address[] memory recipients, uint256[] memory amounts, address token, bytes[] memory data)
        public
        virtual
    {
        _handleBatch(recipients, amounts, token, data, OP_DISTRIBUTE);
    }

    function _handleBatch(
        address[] memory recipients,
        uint256[] memory amounts,
        address token,
        bytes[] memory data,
        bytes32 operation
    ) private {
        uint256 length = recipients.length;
        require(length > 0 && length == amounts.length, "Mismatched lengths");

        for (uint256 i = 0; i < length; i++) {
            bytes memory _data = i < data.length ? data[i] : bytes("");

            if (operation == OP_ALLOCATE) {
                _allocate(recipients[i], amounts[i], token, _data);
            } else if (operation == OP_DISTRIBUTE) {
                _distribute(recipients[i], amounts[i], token, _data);
            } else {
                revert("Invalid operation");
            }
        }
    }

    // Allocate transfers tokens from sender to recipients
    function _allocate(address to, uint256 amount, address token, bytes memory data) internal virtual nonReentrant {
        IERC20(token).safeTransferFrom(_msgSender(), to, amount);
        emit Allocate(_msgSender(), to, amount, token, data);
    }

    // Distribute transfers tokens from the contract to recipients
    function _distribute(address to, uint256 amount, address token, bytes memory data) internal virtual nonReentrant {
        IERC20(token).safeTransfer(to, amount);
        emit Allocate(address(this), to, amount, token, data);
    }
}

contracts/extensions/MerkleClaim.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.20;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

interface IMerkleClaim {
    event MerkleSet(bytes32 root);
    event Claim(address recipient, uint256 amount, address token, bytes data);

    function setMerkle(bytes32 _root) external;
}

abstract contract MerkleClaim is IMerkleClaim {
    using SafeERC20 for IERC20;
    bytes32 private root;
    mapping(bytes32 => mapping(address => bool)) private hasClaimed;

    function _setMerkle(bytes32 _root) internal virtual {
        root = _root;
        emit MerkleSet(root);
    }

    function claim(
        address recipient,
        uint256 amount,
        address token,
        bytes32[] memory proof,
        bytes memory data
    ) public virtual {
        _claim(root, recipient, amount, token, proof, data);
    }

    function _claim(
        bytes32 merkleRoot,
        address recipient,
        uint256 amount,
        address token,
        bytes32[] memory proof,
        bytes memory data
    ) internal virtual {
        // TODO: Do we need to check if merkleRoot has been set first?
        require(!hasClaimed[merkleRoot][recipient], "Already claimed");
        hasClaimed[merkleRoot][recipient] = true;

        bytes32 leaf = keccak256(bytes.concat(keccak256(abi.encode(token, recipient, amount))));
        require(MerkleProof.verify(proof, merkleRoot, leaf), "Invalid proof");
        IERC20(token).safeTransfer(recipient, amount);
        emit Claim(recipient, amount, token, data);
    }
}

contracts/extensions/Registry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IRegistry {
    enum Status {
        pending,
        approved
    }

    struct Registration {
        Status status;
        address owner;
        bytes data; // Data can contain information that can be accessed later
    }

    event Register(address indexed project, uint256 indexed index, string metadataURI, bytes data);
    event Approve(address indexed project, uint256 indexed index, string metadataURI, bytes data);
    event Update(address indexed project, uint256 indexed index, string metadataURI, bytes data);

    function register(address project, string memory metadataURI, bytes memory data) external;
}

contract Registry is IRegistry {
    mapping(address => mapping(uint256 => Registration)) public projects;
    mapping(address => uint256) private indexes;

    // MetadataURI can contain details about project / application / campaign
    function register(address project, string memory metadataURI, bytes memory data) public virtual {
        _register(project, metadataURI, data);
    }

    // MetadataURI can contain review details
    function update(address project, uint256 index, string memory metadataURI, bytes memory data) public virtual {
        _update(project, index, metadataURI, data);
    }

    function approve(address project, uint256 index, string memory metadataURI, bytes memory data) public virtual {
        _approve(project, index, metadataURI, data);
    }

    // Register project / application / campaign
    function _register(address project, string memory metadataURI, bytes memory data) internal virtual {
        uint256 index = indexes[project]++;
        require(projects[project][index].status == Status.pending, "Project already registered");

        // When index is 0 we interpret it as the project registration.
        // This way we have a simple and flexible way to handle
        // project registration, applications, creation of campaigns etc.
        projects[project][index] = Registration(Status.pending, msg.sender, data);
        emit Register(project, index, metadataURI, data);
    }

    // MetadataURI can contain Review information
    function _approve(address project, uint256 index, string memory metadataURI, bytes memory data) internal virtual {
        require(projects[project][index].status == Status.pending, "Project already approved or not registered yet");
        projects[project][index] = Registration(Status.approved, msg.sender, data);
        emit Approve(project, index, metadataURI, data);
    }

    function _update(address project, uint256 index, string memory metadataURI, bytes memory data) internal virtual {
        require(projects[project][index].owner == msg.sender, "Must be owner to update");
        emit Update(project, index, metadataURI, data);
    }
}

